// Find the shortest palindrome that begins with a supplied string.
// Algorithm idea is simple:
// - Find the longest postfix of supplied string that is a palindrome.
// - Append to the end of the string reverse of a string prefix that comes before the palindromic suffix.
// >>> make_palindrome((""))
// ("")
// >>> make_palindrome(("cat"))
// ("catac")
// >>> make_palindrome(("cata"))
// ("catac")
std::string make_palindrome(std::string string) { 
    int n = string.length(); 

    // If string is empty, return an empty string
    if (n == 0)
        return ""; 

    std::string rev_string(string); 
    reverse(rev_string.begin(), rev_string.end()); 

    for (int i = 0; i < n; i++) { 
        // Check if substring starting from index 'i' is a palindrome or not
        if (string.substr(i) == rev_string.substr(0, n - i)) 
            return string + rev_string.substr(n - i); 
    } 

    return string + rev_string.substr(1);

