// (14)
long fib4(long n) { 
    if(n==0 || n==1 || n==3)return 0;
    if(n==2)return 2;
    long a=0,b=0,c=2,d,e;
    for(int i=4;i<=n;i++){
        d=a+b+c;
        e=d+a;
        a=b;
        b=c;
        c=d;
        d=e;
    }
    return d;

