// >>> will_it_fly((std::vector<long>({(long)3, (long)2, (long)3})), (1))
// (false)
// # it's balanced, but 3+2+3 is more than the maximum possible weight.
// >>> will_it_fly((std::vector<long>({(long)3, (long)2, (long)3})), (9))
