// # 3 is less than the maximum possible weight, and it's balanced.
bool will_it_fly(std::vector<long> q, long w) { 
    int n = q.size();
    for(int i=0; i<n/2; i++){
        if(q[i] != q[n-i-1]){
            return false;
        }
    }
    long sum = accumulate(q.begin(), q.end(), 0);
    return sum <= w;

