// (false)
// >>> is_simple_power((3), (1))
// (false)
// >>> is_simple_power((5), (3))
// (false)
bool is_simple_power(long x, long n) { 
    // If x is 1, it's always a simple power for any n
    if (x == 1) return true;

    // If n is 1, the only simple power is 1 itself
    if (n == 1) return false;

    // Calculate the logarithm of x with base n to check if it's an integer
    double log_val = log(x) / log(n);

    // Check if log_val is an integer by comparing it with its rounded value
    return floor(log_val) == ceil(log_val);

