    long count = 0;
    std::istringstream iss(S);
    std::string token;
    while(getline(iss,token,'.')){
        std::istringstream iss2(token);
        std::string token2;
        while(getline(iss2,token2,'?')){
            std::istringstream iss3(token2);
            std::string token3;
            while(getline(iss3,token3,'!')){
                if(token3.find("I ") == 0){
                    count++;
                }
            }
        }
    }
    return count;

