// (std::vector<long>({(long)3, (long)5, (long)7}))
std::vector<long> make_a_pile(long n) { std::vector<long> pile;
    long current_stones = n;
    for(long i = 0; i < n; ++i) {
        pile.push_back(current_stones);
        // If n is odd, add the next odd number, otherwise add the next even number.
        current_stones += (n % 2 == 0) ? 2 : 1;
    }
    return pile;

