std::vector<long> get_odd_collatz(long n) { 
    std::vector<long> result;
    while(n != 1){
        if(n % 2 == 0)
            n /= 2;
        else{
            result.push_back(n);
            n = 3 * n + 1;
        }
    }
    result.push_back(1);
    sort(result.begin(),result.end());
