// >>> fix_spaces((" Example 3"))
// ("_Example-3")
std::string fix_spaces(std::string text) { 
    std::string result = ""; 
    int spaceCount = 0; 

    for (int i=0; i < text.length(); i++) { 
        if (text[i] == ' ') { 
            spaceCount++; 
        } else { 
            if (spaceCount > 2) { 
                result += "-"; 
            } else { 
                while (spaceCount--) { 
                    result += "_"; 
                } 
            } 
            spaceCount = 0; 
            result += text[i]; 
        } 
    } 

    // If the last character is a space 
    if (spaceCount > 2) { 
        result += "-"; 
    } else { 
        while (spaceCount--) { 
            result += "_"; 
        } 
    } 

    return result;

