std::vector<long> order_by_points(std::vector<long> nums) { 
    std::sort(nums.begin(),nums.end(),[](long a,long b){
        long sa=0,sb=0;
        for(long x=a;x!=0;x/=10)
            sa+=abs(x%10);
        for(long x=b;x!=0;x/=10)
            sb+=abs(x%10);
        return sa==sb?a<b:a<sa;
    });
    return nums;

