// (std::vector<std::string>({(std::string)"ab", (std::string)"cd"}))
std::vector<std::string> sorted_list_sum(std::vector<std::string> lst) { 
    std::sort(lst.begin(),lst.end());
    for(int i=0;i<lst.size();i++){
        if(lst[i].length()%2!=0){
            lst.erase(lst.begin()+i);
            i--;
        }
    }
    return lst;

