// Given a vector of numbers, return the sum of squares of the numbers
// in the vector that are odd. Ignore numbers that are negative or not integers.
// >>> double_the_difference((std::vector<float>({(long)1, (long)3, (long)2, (long)0})))
// (10)
// >>> double_the_difference((std::vector<float>({(long)-1, (long)-2, (long)0})))
// (0)
// >>> double_the_difference((std::vector<float>({(long)9, (long)-2})))
// (81)
// >>> double_the_difference((std::vector<float>({(long)0})))
// (0)
// If the input vector is empty, return 0.
long double_the_difference(std::vector<float> lst) { 
    long res = 0;
    for(auto x : lst){
        if(x >= 0 && int(x) == x && int(x)%2 != 0){
            res += pow(x, 2);
        }
    }
    return res;

