// (std::make_tuple(1, 1))
// >>> even_odd_count((123))
// (std::make_tuple(1, 2))
std::tuple<long, long> even_odd_count(long num) { 
    std::string s = std::to_string(std::abs(num));
    long evenCount = 0, oddCount = 0;
    for(char c : s){
        if((c - '0') % 2 == 0)
            evenCount++;
        else
            oddCount++;
    }
    return std::make_tuple(evenCount, oddCount);

