    int max = 0;
    std::string res;
    for(int i=0;i<words.size();i++){
        s.clear();
        for(int j=0;j<words[i].size();j++)
            s.insert(words[i][j]);
        if(s.size()>max){
            max=s.size();
            res=words[i];
        }
    }
    return res;

