// @need : integer
// the number of carrots that you need to eat.
// @remaining : integer
// the number of remaining carrots thet exist in stock
// Constrain:
// * 0 <= number <= 1000
// * 0 <= need <= 1000
// * 0 <= remaining <= 1000
// Have fun :)
std::vector<long> eat(long number, long need, long remaining) { 
    long total_eaten = number + std::min(need, remaining);
    long carrots_left = std::max(remaining - need, 0L);
    return std::vector<long>({total_eaten, carrots_left});

