function modp(n, p){ 
    return Math.pow(2,n) % p;
}

//remove_vowels is a function that takes string and returns string without vowels.
// >>> remove_vowels("")
// ""
// >>> remove_vowels("abcdef")
// "bcdf"
// >>> remove_vowels("aaaaa")
// ""
// >>> remove_vowels("aaBAA")
// "B"
// >>> remove_vowels("zbcd")
// "zbcd"
function remove_vowels(text){ 
    let result = '';
    for(let i=0; i<text.length; i++){
