//Return n-th Fibonacci number.
// >>> fib(10)
// 55
// >>> fib(1)
// 1
// >>> fib(8)
// 21
function fib(n){ 
    if (n <= 1) {
        return n;
    }
    let a = 0, b = 1, c;
    for(let i = 2; i <= n; i++){
        c = a + b;
        a = b;
        b = c;
    }
    return b;
}

