//Return true is array elements are monotonically increasing or decreasing.
// >>> monotonic([1, 2, 4, 20])
// true
// >>> monotonic([1, 20, 4, 10])
// false
// >>> monotonic([4, 1, 0, -10])
// true
function monotonic(l){ 
    let a = l[0], b = l[l.length-1]; 
    if (a < b) { 
        for(let i=1; i<l.length; i++) 
            if(l[i] < l[i-1]) return false; 
        return true; 
    } else { 
        for(let i=1; i<l.length; i++) 
            if(l[i] > l[i-1]) return false; 
        return true; 
    } 
}

