// 2
// >>> search([1, 2, 2, 3, 3, 3, 4, 4, 4])
// 3
// >>> search([5, 5, 4, 4, 4])
// -1
function search(lst){ 
    let freq = {};
    for(let i=0;i<lst.length;i++){
        if(freq[lst[i]]){
            freq[lst[i]]++;
        }else{
            freq[lst[i]]=1;
        }
    }
