function starts_one_ends(n){ 
    if (n === 1) {
        return 2; // Only two numbers: 1 and 10
    } else {
        let totalNumbers = Math.pow(10, n);
        let numbersStartingWithOne = Math.pow(10, n - 1);
        let numbersEndingWithOne = Math.pow(10, n - 1);
        let numbersBothStartAndEndWithOne = Math.pow(10, n - 2);

        return numbersStartingWithOne + numbersEndingWithOne - numbersBothStartAndEndWithOne;
