//You are given a 2 dimensional data, as a nested arrays,
// which is similar to matrix, however, unlike matrices,
// each row may contain a different number of columns.
// Given lst, and integer x, find integers x in the array,
// and return array of arrays, [(x1, y1), (x2, y2) ...] such that
// each array is a coordinate - (row, columns), starting with 0.
// Sort coordinates initially by rows in ascending order.
// Also, sort coordinates of the row by columns in descending order.
// Examples:
// >>> get_row([[1, 2, 3, 4, 5, 6], [1, 2, 3, 4, 1, 6], [1, 2, 3, 4, 5, 1]], 1)
// [[0, 0], [1, 4], [1, 0], [2, 5], [2, 0]]
// >>> get_row([], 1)
// []
// >>> get_row([[], [1], [1, 2, 3]], 3)
// [[2, 2]]
function get_row(lst, x){ 
    let result = [];
    for(let i=0; i<lst.length; i++){
        for(let j=0; j<lst[i].length; j++){
            if(lst[i][j] === x){
                result.push([i,j]);
            }
        }
    }
    result.sort((a,b)=>{
        if(a[0]===b[0]){
            return b[1]-a[1];
        }else{
            return a[0]-b[0];
        }
    });
    return result;
}

//Given an array of non-negative integers, return a cojs of the given array after sorting,
// you will sort the given array in ascending order if the sum( first index value, last index value) is odd,
// or sort it in descending order if the sum( first index value, last index value) is even.
// Note:
// * don't change the given array.
// Examples:
// >>> sort_array([])
// []
// >>> sort_array([5])
// [5]
// >>> sort_array([2, 4, 3, 0, 1, 5])
// [0, 1, 2, 3, 4, 5]
// >>> sort_array([2, 4, 3, 0, 1, 5, 6])
// [6, 5, 4, 3, 2, 1, 0]
function sort_array(array){ 
    let newArray = [...array];
    if(newArray.length === 0 || newArray.length === 1){
        return newArray;
    }
    else{
        if((newArray[0] + newArray[newArray.length - 1]) % 2 === 0){
            newArray.sort(function(a, b){return b - a});
        }
        else{
            newArray.sort(function(a, b){return a - b});
        }
    }
    return newArray;
}

//Create a function encrypt that takes a string as an argument and
// returns a string encrypted with the alphabet being rotated. 
// The alphabet should be rotated in a manner such that the letters 
// shift down by two multiplied to two places.
// For example:
// >>> encrypt("hi")
// "lm"
// >>> encrypt("asdfghjkl")
// "ewhjklnop"
// >>> encrypt("gf")
// "kj"
// >>> encrypt("et")
// "ix"
function encrypt(s){  
    let str = '';
    for(let i=0; i<s.length; i++){
        let charCode = s.charCodeAt(i);
        if(charCode >= 97 && charCode <= 122){
            str += String.fromCharCode((charCode - 97 + (2 * 2) % 26) % 26 + 97);
        }else if(charCode >= 65 && charCode <= 90){
            str += String.fromCharCode((charCode - 65 + (2 * 2) % 26) % 26 + 65);
        }
    }
    return str;
}

//You are given an array of integers.
// Write a function next_smallest() that returns the 2nd smallest element of the array.
// Return undefined if there is no such element.
// >>> next_smallest([1, 2, 3, 4, 5])
// 2
// >>> next_smallest([5, 1, 4, 3, 2])
// 2
// >>> next_smallest([])
// undefined
// >>> next_smallest([1, 1])
// undefined
function next_smallest(lst){ 
    let sorted = lst.sort((a,b) => a - b);
    for(let i = 0; i < sorted.length; i++){
        if(sorted[i] !== sorted[i+1]){
            return sorted[i + 1];
        }
    }
    return undefined;
}

//You'll be given a string of words, and your task is to count the number
// of boredoms. A boredom is a sentence that starts with the word "I".
// Sentences are delimited by '.', '?' or '!'.
// For example:
// >>> is_bored("Hello world")
// 0
// >>> is_bored("The sky is blue. The sun is shining. I love this weather")
// 1
function is_bored(S){ 
    let arr = S.split(/[.?!]/);
    let count = 0;
    for(let i=0; i<arr.length; i++){
        if(arr[i].trim().startsWith('I')){
            count++;
        }
    }
    return count;
}

//Create a function that takes 3 numbers.
// Returns true if one of the numbers is equal to the sum of the other two, and all numbers are integers.
// Returns false in any other cases.
// Examples
// >>> any_int(5, 2, 7)
// true
// >>> any_int(3, 2, 2)
// false
// >>> any_int(3, -2, 1)
// true
// >>> any_int(3.6, -2.2, 2)
// false
function any_int(x, y, z){ 
    return (Number.isInteger(x) && Number.isInteger(y) && Number.isInteger(z)) && ((x == y + z) || (y == x + z) || (z == x + y));
}

//Write a function that takes a message, and encodes in such a 
// way that it swaps case of all letters, replaces all vowels in 
// the message with the letter that appears 2 places ahead of that 
// vowel in the english alphabet. 
// Assume only letters. 
// Examples:
// >>> encode("test")
// "TGST"
// >>> encode("This is a message")
// "tHKS KS C MGSSCGG"
function encode(message){ 
    let encodedMessage = '';
    for(let i=0; i<message.length; i++){
        let char = message[i];
        if(char === char.toUpperCase()){
            char = char.toLowerCase();
        } else {
            char = char.toUpperCase();
        }
        if(char === 'a' || char === 'A'){
            char = 'C';
        } else if(char === 'e' || char === 'E'){
            char = 'G';
        } else if(char === 'i' || char === 'I'){
            char = 'K';
        } else if(char === 'o' || char === 'O'){
            char = 'Q';
        } else if(char === 'u' || char === 'U'){
            char = 'W';
        }
        encodedMessage += char;
    }
    return encodedMessage;
}

//You are given an array of integers.
// You need to find the largest prime value and return the sum of its digits.
// Examples:
// >>> skjkasdkd([0, 3, 2, 1, 3, 5, 7, 4, 5, 5, 5, 2, 181, 32, 4, 32, 3, 2, 32, 324, 4, 3])
// 10
// >>> skjkasdkd([1, 0, 1, 8, 2, 4597, 2, 1, 3, 40, 1, 2, 1, 2, 4, 2, 5, 1])
// 25
// >>> skjkasdkd([1, 3, 1, 32, 5107, 34, 83278, 109, 163, 23, 2323, 32, 30, 1, 9, 3])
// 13
// >>> skjkasdkd([0, 724, 32, 71, 99, 32, 6, 0, 5, 91, 83, 0, 5, 6])
// 11
// >>> skjkasdkd([0, 81, 12, 3, 1, 21])
// 3
// >>> skjkasdkd([0, 8, 1, 2, 1, 7])
// 7
function skjkasdkd(lst){ 
    let maxPrime = -1;
    for(let i=0; i<lst.length; i++){
        if(isPrime(lst[i])){
            maxPrime = Math.max(maxPrime, lst[i]);
        }
    }
    return sumOfDigits(maxPrime);
}

//Given an object, return true if all keys are strings in lower 
// case or all keys are strings in upper case, else return false.
// The function should return false is the given object is empty.
// Examples:
// >>> check_dict_case({"a": "apple", "b": "banana"})
// true
// >>> check_dict_case({"a": "apple", "A": "banana", "B": "banana"})
// false
// >>> check_dict_case({"a": "apple", 8: "banana", "a": "apple"})
// false
// >>> check_dict_case({"Name": "John", "Age": "36", "City": "Houston"})
// false
// >>> check_dict_case({"STATE": "NC", "ZIP": "12345"})
// true
function check_dict_case(dict){  
    let keys = Object.keys(dict);
    if(keys.length === 0) return false;
    let allLowerCase = keys.every(key => typeof key === 'string' && key.toLowerCase() === key);
    let allUpperCase = keys.every(key => typeof key === 'string' && key.toUpperCase() === key);
    return allLowerCase || allUpperCase;
}

//Implement a function that takes an non-negative integer and returns an array of the first n
// integers that are prime numbers and less than n.
// for example:
// >>> count_up_to(5)
// [2, 3]
// >>> count_up_to(11)
// [2, 3, 5, 7]
// >>> count_up_to(0)
// []
// >>> count_up_to(20)
// [2, 3, 5, 7, 11, 13, 17, 19]
// >>> count_up_to(1)
// []
// >>> count_up_to(18)
// [2, 3, 5, 7, 11, 13, 17]
function count_up_to(n){ 
    let primes = [];
    for(let i=2; i<n; i++){
        if(isPrime(i)){
            primes.push(i);
        }
    }
    return primes;
}

