    if ((month == 1 || month == 3 || month == 5 || month == 7 || month == 8 || month == 10 || month == 12) && day > 31) return false;
    if (month == 2){
        if (((year % 4 == 0) && (year % 100 != 0)) || (year % 400 == 0)){
            if (day > 29) return false;
        } else {
            if (day > 28) return false;
        }
    }

    return true;
}

