// are met:
// - There should not be more than three digits ('0'-'9') in the file's name.
// - The file's name contains exactly one dot '.'
// - The substring before the dot should not be empty, and it starts with a letter from 
// the latin alphapet ('a'-'z' and 'A'-'Z').
// - The substring after the dot should be one of these: ['txt', 'exe', 'dll']
// Examples:
// >>> file_name_check("example.txt")
// "Yes"
// >>> file_name_check("1example.dll")
// "No"
function file_name_check(file_name){ 
    let digitCount = 0;
    let dotCount = 0;
    let alphaStart = false;
    let validExtensions = ['txt', 'exe', 'dll'];

    for(let i = 0; i < file_name.length; i++){
        if(file_name[i] >= '0' && file_name[i] <= '9'){
            digitCount++;
        }
        else if(file_name[i] == '.'){
            dotCount++;
        }
        else if(i == 0 && (file_name[i] >= 'a' && file_name[i] <= 'z' || file_name[i] >= 'A' && file_name[i] <= 'Z')){
            alphaStart = true;
        }
    }

    let extension = file_name.split('.').pop();
    if(validExtensions.includes(extension)){
        return digitCount <= 3 && dotCount == 1 && alphaStart ? 'Yes' : 'No';
    }else{
        return 'No';
    }
}

