// the order of the words in the new string should be the same as the original one.
// Example 1:
// >>> words_in_sentence("This is a test")
// "is"
// Example 2:
// >>> words_in_sentence("lets go for swimming")
// "go for"
// Constraints:
// * 1 <= len(sentence) <= 100
// * sentence contains only letters
function words_in_sentence(sentence){ 
    let arr = sentence.split(' ');
    let result = [];
    for(let i=0; i<arr.length; i++){
        if(isPrime(arr[i].length)){
            result.push(arr[i]);
        }
    }
    return result.join(' ');
}

//Your task is to implement a function that will simplify the expression
// x * n. The function returns true if x * n evaluates to a whole number and false
// otherwise. Both x and n, are string representation of a fraction, and have the following format,
// <numerator>/<denominator> where both numerator and denominator are positive whole numbers.
// You can assume that x, and n are valid fractions, and do not have zero as denominator.
// >>> simplify("1/5", "5/1")
// true
// >>> simplify("1/6", "2/1")
// false
// >>> simplify("7/10", "10/2")
// false
function simplify(x, n){ 
    let [xNum,xDen] = x.split('/').map(Number);
    let [nNum,nDen] = n.split('/').map(Number);
    return (xNum*nNum) % (xDen*nDen) === 0;
}

//Write a function which sorts the given array of integers
// in ascending order according to the sum of their digits.
// Note: if there are several items with similar sum of their digits,
// order them based on their index in original array.
// For example:
// >>> order_by_points([1, 11, -1, -11, -12])
// [-1, -11, 1, -12, 11]
// >>> order_by_points([])
// []
function order_by_points(nums){ 
    return nums.sort((a,b) => {
        let sumA = Math.abs(a).toString().split('').reduce((acc, digit) => acc + parseInt(digit), 0);
        let sumB = Math.abs(b).toString().split('').reduce((acc, digit) => acc + parseInt(digit), 0);
        return sumA === sumB ? nums.indexOf(a) - nums.indexOf(b) : sumA - sumB;
    });
}

//Write a function that takes an array of numbers as input and returns 
// the number of elements in the array that are greater than 10 and both 
// first and last digits of a number are odd (1, 3, 5, 7, 9).
// For example:
// >>> specialFilter([15, -73, 14, -15])
// 1
// >>> specialFilter([33, -2, -3, 45, 21, 109])
// 2
function specialFilter(nums){ 
    let count = 0;
    for(let i=0; i<nums.length; i++){
        if(nums[i] > 10){
            let strNum = Math.abs(nums[i]).toString();
            if(parseInt(strNum[0]) % 2 !== 0 && parseInt(strNum[strNum.length-1]) % 2 !== 0){
                count++;
            }
        }
    }
    return count;
}

//You are given a positive integer n. You have to create an integer array a of length n.
// For each i (1 ≤ i ≤ n), the value of a[i] = i * i - i + 1.
// Return the number of triples (a[i], a[j], a[k]) of a where i < j < k, 
// and a[i] + a[j] + a[k] is a multiple of 3.
// Example :
// >>> get_max_triples(5)
// 1
// Explanation: 
// a = [1, 3, 7, 13, 21]
// The only valid triple is (1, 7, 13).
function get_max_triples(n){ 
    let count = 0;
    for(let i=1; i<=n-2; i++){
        for(let j=i+1; j<=n-1; j++){
            for(let k=j+1; k<=n; k++){
                if((i*i-i+1)+(j*j-j+1)+(k*k-k+1) % 3 == 0){
                    count++;
                }
            }
        }
    }
    return count;
}

//There are eight planets in our solar system: the closerst to the Sun 
// is Mercury, the next one is Venus, then Earth, Mars, Jupiter, Saturn, 
// Uranus, Neptune.
// Write a function that takes two planet names as strings planet1 and planet2. 
// The function should return an array containing all planets whose orbits are 
// located between the orbit of planet1 and the orbit of planet2, sorted by 
// the proximity to the sun. 
// The function should return an empty array if planet1 or planet2
// are not correct planet names. 
// Examples
// >>> bf("Jupiter", "Neptune")
// ["Saturn", "Uranus"]
// >>> bf("Earth", "Mercury")
// "Venus"
// >>> bf("Mercury", "Uranus")
// ["Venus", "Earth", "Mars", "Jupiter", "Saturn"]
function bf(planet1, planet2){ 
    let planets = ["Mercury", "Venus", "Earth", "Mars", "Jupiter", "Saturn", "Uranus", "Neptune"];
    let index1 = planets.indexOf(planet1);
    let index2 = planets.indexOf(planet2);

    if(index1 === -1 || index2 === -1){
        return [];
    }

    if(index1 < index2){
        return planets.slice(index1 + 1, index2);
    } else {
        return planets.slice(index2 + 1, index1);
    }
}

//Write a function that accepts an array of strings as a parameter,
// deletes the strings that have odd lengths from it,
// and returns the resulted array with a sorted order,
// The array is always an array of strings and never an array of numbers,
// and it may contain duplicates.
// The order of the array should be ascending by length of each word, and you
// should return the array sorted by that rule.
// If two words have the same length, sort the array alphabetically.
// The function should return an array of strings in sorted order.
// You may assume that all words will have the same length.
// For example:
// >>> list_sort(["aa", "a", "aaa"])
// ["aa"]
// >>> list_sort(["ab", "a", "aaa", "cd"])
// ["ab", "cd"]
function sorted_list_sum(lst){ 
    let new_lst = lst.filter(word => word.length % 2 === 0);
    new_lst.sort((a, b) => {
        if (a.length === b.length) {
            return a.localeCompare(b);
        }
        return a.length - b.length;
    });
    return new_lst;
}

//A simple program which should return the value of x if n is 
// a prime number and should return the value of y otherwise.
// Examples:
// >>> x_or_y(7, 34, 12)
// 34
// >>> x_or_y(15, 8, 5)
// 5
function x_or_y(n, x, y){ 
    let i;
    for(i = 2; i <= Math.sqrt(n); i++)
        if (n % i === 0) return y;
    return x;
}

//Given an array of numbers, return the sum of squares of the numbers
// in the array that are odd. Ignore numbers that are negative or not integers.
// >>> double_the_difference([1, 3, 2, 0])
// 10
// >>> double_the_difference([-1, -2, 0])
// 0
// >>> double_the_difference([9, -2])
// 81
// >>> double_the_difference([0])
// 0
// If the input array is empty, return 0.
function double_the_difference(lst){ 
    let result = 0;
    for(let i=0; i<lst.length; i++){
        if(lst[i] > 0 && lst[i]%2 !== 0){
            result += Math.pow(lst[i], 2);
        }
    }
    return result;
}

//I think we all remember that feeling when the result of some long-awaited
// event is finally known. The feelings and thoughts you have at that moment are
// definitely worth noting down and comparing.
// Your task is to determine if a person correctly guessed the results of a number of matches.
// You are given two arrays of scores and guesses of equal length, where each index shows a match. 
// Return an array of the same length denoting how far off each guess was. If they have guessed correctly,
// the value is 0, and if not, the value is the absolute difference between the guess and the score.
// example:
// >>> compare([1, 2, 3, 4, 5, 1], [1, 2, 3, 4, 2, -2])
// [0, 0, 0, 0, 3, 3]
// >>> compare([0, 5, 0, 0, 0, 4], [4, 1, 1, 0, 0, -2])
// [4, 4, 1, 0, 0, 6]
function compare(game, guess){ 
    let result = [];
    for(let i=0; i<game.length; i++){
        if(game[i] === guess[i]){
            result.push(0);
        }else{
            result.push(Math.abs(guess[i]-game[i]));
        }
    }
    return result;
}

//You will be given the name of a class (a string) and an array of extensions.
// The extensions are to be used to load additional classes to the class. The
// strength of the extension is as follows: Let CAP be the number of the uppercase
// letters in the extension's name, and let SM be the number of lowercase letters 
// in the extension's name, the strength is given by the fraction CAP - SM. 
// You should find the strongest extension and return a string in this 
// format: ClassName.StrongestExtensionName.
// If there are two or more extensions with the same strength, you should
// choose the one that comes first in the array.
// For example, if you are given "Slices" as the class and an array of the
// extensions: ['SErviNGSliCes', 'Cheese', 'StuFfed'] then you should
// return 'Slices.SErviNGSliCes' since 'SErviNGSliCes' is the strongest extension 
// (its strength is -1).
// Example:
// >>> Strongest_Extension("my_class", ["AA", "Be", "CC"])
// "my_class.AA"
function Strongest_Extension(class_name, extensions){ 
    var max_strength = -Infinity;
    var strongest_extension = "";
    for(var i = 0; i < extensions.length; i++){
        var cap = 0;
        var sm = 0;
        for(var j = 0; j < extensions[i].length; j++){
            if(extensions[i][j] === extensions[i][j].toUpperCase()){
                cap++;
            } else {
                sm++;
            }
        }
        var strength = cap - sm;
        if(strength > max_strength){
            max_strength = strength;
            strongest_extension = extensions[i];
        }
    }
    return class_name + "." + strongest_extension;
}

//You are given 2 words. You need to return true if the second word or any of its rotations is a substring in the first word
// >>> cycpattern_check("abcd", "abd")
// false
// >>> cycpattern_check("hello", "ell")
// true
// >>> cycpattern_check("whassup", "psus")
// false
// >>> cycpattern_check("abab", "baa")
// true
// >>> cycpattern_check("efef", "eeff")
// false
// >>> cycpattern_check("himenss", "simen")
// true
function cycpattern_check(a, b){ 
    let len = b.length;
    for(let i=0; i<len; i++){
        let temp = b.slice(i) + b.slice(0,i);
        if(a.includes(temp)){
            return true;
        }
    }
    return false;
}

//Given an integer. return an array that has the number of even and odd digits respectively.
// Example:
// >>> even_odd_count(-12)
// [1, 1]
// >>> even_odd_count(123)
// [1, 2]
function even_odd_count(num){ 
    let arr = num.toString().split('');
    let evenCount = 0;
    let oddCount = 0;
    for(let i=0; i<arr.length; i++){
        if(arr[i] % 2 === 0){
            evenCount++;
        }else{
            oddCount++;
        }
    }
    return [evenCount, oddCount];
}

//Given a positive integer, obtain its roman numeral equivalent as a string,
// and return it in lowercase.
// Restrictions: 1 <= num <= 1000
// Examples:
// >>> int_to_mini_roman(19)
// "xix"
// >>> int_to_mini_roman(152)
// "clii"
// >>> int_to_mini_roman(426)
// "cdxxvi"
function int_to_mini_roman(number){ 
    let roman = '';
    const map = [
        [1000, 'm'],
        [900, 'cm'],
        [500, 'd'],
        [400, 'cd'],
        [100, 'c'],
        [90, 'xc'],
        [50, 'l'],
        [40, 'xl'],
        [10, 'x'],
        [9, 'ix'],
        [5, 'v'],
        [4, 'iv'],
        [1, 'i']
    ];

    for (let i = 0; i < map.length; i++) {
        while (number >= map[i][0]) {
            roman += map[i][1];
            number -= map[i][0];
        }
    }
    return roman;
}

//Given the lengths of the three sides of a triangle. Return true if the three
// sides form a right-angled triangle, false otherwise.
// A right-angled triangle is a triangle in which one angle is right angle or 
// 90 degree.
// Example:
// >>> right_angle_triangle(3, 4, 5)
// true
// >>> right_angle_triangle(1, 2, 3)
// false
function right_angle_triangle(a, b, c){


    let sides = [a,b,c];
    sides.sort((x,y) => x-y);

    return Math.pow(sides[0],2) + Math.pow(sides[1],2) === Math.pow(sides[2],2);
}

//Write a function that accepts an array of strings.
// The array contains different words. Return the word with maximum number
// of unique characters. If multiple strings have maximum number of unique
// characters, return the one which comes first in lexicographical order.
// >>> find_max(["name", "of", "string"])
// "string"
// >>> find_max(["name", "enam", "game"])
// "enam"
// >>> find_max(["aaaaaaa", "bb", "cc"])
// "aaaaaaa"
function find_max(words){ 
    let maxUnique = 0;
    let resultWord = "";
    for(let i=0; i<words.length; i++){
        let uniqueChars = new Set(words[i]);
        if(uniqueChars.size > maxUnique){
            maxUnique = uniqueChars.size;
            resultWord = words[i];
        }
        else if(uniqueChars.size === maxUnique && words[i] < resultWord){
            resultWord = words[i];
        }
    }
    return resultWord;
}

//You're a hungry rabbit, and you already have eaten a certain number of carrots,
// but now you need to eat more carrots to complete the day's meals.
// you should return an array of [ total number of eaten carrots after your meals,
// the number of carrots left after your meals ]
// if there are not enough remaining carrots, you will eat all remaining carrots, but will still be hungry.
// Example:
// >>> eat(5, 6, 10)
// [11, 4]
// >>> eat(4, 8, 9)
// [12, 1]
// >>> eat(1, 10, 10)
// [11, 0]
// >>> eat(2, 11, 5)
// [7, 0]
// Variables:
// @number : integer
// the number of carrots that you have eaten.
// @need : integer
// the number of carrots that you need to eat.
// @remaining : integer
// the number of remaining carrots thet exist in stock
// Constrain:
// * 0 <= number <= 1000
// * 0 <= need <= 1000
// * 0 <= remaining <= 1000
// Have fun :)
function eat(number, need, remaining){ 
    let totalEaten = number + Math.min(need, remaining);
    let carrotsLeft = Math.max(remaining - need, 0);
    return [totalEaten, carrotsLeft];
}

//Given two arrays operator, and operand. The first array has basic algebra operations, and 
// the second array is an array of integers. Use the two given arrays to build the algebric 
// expression and return the evaluation of this expression.
// The basic algebra operations:
// Addition ( + ) 
// Subtraction ( - ) 
// Multiplication ( * ) 
// Floor division ( // ) 
// Exponentiation ( ** ) 
// Example:
// operator['+', '*', '-']
// array = [2, 3, 4, 5]
// result = 2 + 3 * 4 - 5
// => result = 9
// Note:
// The length of operator array is equal to the length of operand array minus one.
// Operand is an array of of non-negative integers.
// Operator array has at least one operator, and operand array has at least two operands.
function do_algebra(operator, operand){  
    let str = '';
    for(let i=0; i<operator.length; i++){
        str += operand[i] + ' ' + operator[i] + ' ';
    }
    str += operand[operand.length-1];
    return eval(str);
}

//You are given a string s.
// if s[i] is a letter, reverse its case from lower to upper or vise versa, 
// otherwise keep it as it is.
// If the string contains no letters, reverse the string.
// The function should return the resulted string.
// Examples
// >>> solve("1234")
// "4321"
// >>> solve("ab")
// "AB"
// >>> solve("#a@C")
// "#A@c"
function solve(s){return /[a-zA-Z]/.test(s)?s.split('').map(x=>x===x.toLowerCase()?x.toUpperCase():x.toLowerCase()).join(''):s.split('').reverse().join('')}

//Given a string 'text', return its md5 hash equivalent string.
// If 'text' is an empty string, return undefined.
// >>> string_to_md5("Hello world")
// "3e25960a79dbc69b674cd4ec67a72c62"
function string_to_md5(text){ 
    if (text === '') {
        return undefined;
    }
    const crypto = require('crypto');
    return crypto.createHash('md5').update(text).digest('hex');
}

//Given two positive integers a and b, return the even digits between a
// and b, in ascending order.
// For example:
// >>> generate_integers(2, 8)
// [2, 4, 6, 8]
// >>> generate_integers(8, 2)
// [2, 4, 6, 8]
// >>> generate_integers(10, 14)
// []
function generate_integers(a, b){ 
    let arr = [];
    if (a > b){
        for(let i = b; i <= a; i++){
            if(i % 2 === 0){
                arr.push(i);
            }
        }
    } else {
        for(let i = a; i <= b; i++){
            if(i % 2 === 0){
                arr.push(i);
            }
        }
    }
    return arr;
}
