//Return the number of times the digit 7 appears in integers less than n which are divisible by 11 or 13.
// >>> fizz_buzz(50)
// 0
// >>> fizz_buzz(78)
// 2
// >>> fizz_buzz(79)
// 3
function fizz_buzz(n){ 
    let count = 0;
    for(let i=1; i<n; i++){
        if(i % 11 === 0 || i % 13 === 0){
            let str = i.toString();
            for(let j=0; j<str.length; j++){
                if(str[j] === '7'){
                    count++;
                }
            }
        }
    }
    return count;
}

//This function takes an array l and returns an array l' such that
// l' is identical to l in the odd indicies, while its values at the even indicies are equal
// to the values of the even indicies of l, but sorted.
// >>> sort_even([1, 2, 3])
// [1, 2, 3]
// >>> sort_even([5, 6, 3, 4])
// [3, 6, 5, 4]
function sort_even(l){ 
    let arr = [...l];
    for(let i=0; i<arr.length; i+=2){
        arr[i] = Math.min(...arr.slice(i, arr.length).filter(x => x%2===i));
    }
    return arr;
}

//prime_fib returns n-th number that is a Fibonacci number and it's also prime.
// >>> prime_fib(1)
// 2
// >>> prime_fib(2)
// 3
// >>> prime_fib(3)
// 5
// >>> prime_fib(4)
// 13
// >>> prime_fib(5)
// 89
function prime_fib(n){ 
    let fib = [0, 1];
    let i = 2;
    while (true) {
        fib[i] = fib[i - 1] + fib[i - 2];
        if (isPrime(fib[i])) {
            n--;
            if (n === 0) return fib[i];
        }
        i++;
    }
}

//triples_sum_to_zero takes an array of integers as an input.
// it returns true if there are three distinct elements in the array that
// sum to zero, and false otherwise.
// >>> triples_sum_to_zero([1, 3, 5, 0])
// false
// >>> triples_sum_to_zero([1, 3, -2, 1])
// true
// >>> triples_sum_to_zero([1, 2, 3, 7])
// false
// >>> triples_sum_to_zero([2, 4, -5, 3, 9, 7])
// true
// >>> triples_sum_to_zero([1])
// false
function triples_sum_to_zero(l){ 
    for(let i = 0; i < l.length-2; i++){
        for(let j = i+1; j < l.length-1; j++){
            for(let k = j+1; k < l.length; k++){
                if(l[i] + l[j] + l[k] === 0){
                    return true;
                }
            }
        }
    }
    return false;
}

