//Return true is array elements are monotonically increasing or decreasing.
// >>> monotonic([1, 2, 4, 20])
// true
// >>> monotonic([1, 20, 4, 10])
// false
// >>> monotonic([4, 1, 0, -10])
// true
function monotonic(l){ 
    let inc = false; 
    let dec = false; 

    for(let i=0;i<l.length-1;i++) { 
        if(l[i] < l[i+1]) 
            inc = true; 

        if(l[i] > l[i+1]) 
            dec = true; 
    } 

    return !(inc && dec); 
}

