//Given a positive integer n, return the count of the numbers of n-digit
// positive integers that start or end with 1.
function starts_one_ends(n){ 
    if (n === 1) {
        return 2; // Only two numbers: 1 and 10
    } else {
        // For n > 1:
        // Numbers starting with 1: 1xxxxx... (9 options for each digit)
        // Numbers ending with 1 but not starting with 1: xxxx...1 (8 options for each digit except the first one)
        return Math.pow(9, n - 1) + Math.pow(9, n - 1) - Math.pow(8, n - 1);
    }
}

