// true
// >>> is_sorted([1, 2, 3, 4, 5])
// true
// >>> is_sorted([1, 3, 2, 4, 5])
// false
// >>> is_sorted([1, 2, 3, 4, 5, 6])
// true
// >>> is_sorted([1, 2, 3, 4, 5, 6, 7])
// true
// >>> is_sorted([1, 3, 2, 4, 5, 6, 7])
// false
// >>> is_sorted([1, 2, 2, 3, 3, 4])
// true
// >>> is_sorted([1, 2, 2, 2, 3, 4])
// false
function is_sorted(lst){ 
    for(let i=0; i<lst.length-1;i++){
        if(lst[i]>lst[i+1]){
            return false;
        }
    }
    let count = {};
    for(let i=0; i<lst.length;i++){
        if(count[lst[i]]){
            count[lst[i]]++;
        }else{
            count[lst[i]]=1;
        }
    }
    for(let key in count){
        if(count[key]>1){
            return false;
        }
    }
    return true;
}

//You are given two intervals,
// where each interval is a pair of integers. For example, interval = (start, end) = (1, 2).
// The given intervals are closed which means that the interval (start, end)
// includes both start and end.
// For each given interval, it is assumed that its start is less or equal its end.
// Your task is to determine whether the length of intersection of these two 
// intervals is a prime number.
// Example, the intersection of the intervals (1, 3), (2, 4) is (2, 3)
// which its length is 1, which not a prime number.
// If the length of the intersection is a prime number, return "YES",
// otherwise, return "NO".
// If the two intervals don't intersect, return "NO".
// [input/output] samples:
// >>> intersection([1, 2], [2, 3])
// "NO"
// >>> intersection([-1, 1], [0, 4])
// "NO"
// >>> intersection([-3, -1], [-5, 5])
// "YES"
function intersection(interval1, interval2){  
    let startMax = Math.max(interval1[0], interval2[0]);
    let endMin = Math.min(interval1[1], interval2[1]);
    if(startMax > endMin){
        return 'NO';
    }
    let lengthOfIntersection = endMin - startMax + 1;
    function isPrime(num){
        for(let i=2; i<=Math.sqrt(num); i++){
            if(num % i === 0){
                return false;
            }
        }
        return num > 1;
    }
    return isPrime(lengthOfIntersection) ? 'YES' : 'NO';
}

//You are given an array arr of integers and you need to return
// sum of magnitudes of integers multiplied by product of all signs
// of each number in the array, represented by 1, -1 or 0.
// Note: return undefined for empty arr.
// Example:
// >>> prod_signs([1, 2, 2, -4])
// 9
// >>> prod_signs([0, 1])
// 0
// >>> prod_signs([])
// undefined
function prod_signs(arr){ 
    if (arr.length === 0) {
        return undefined;
    }
    let signProduct = 1;
    let magnitudeSum = 0;
    for(let i=0; i<arr.length; i++){
        if(arr[i]===0){
            return 0;
        } else if(arr[i]<0){
            signProduct *= -1;
        }
        magnitudeSum += Math.abs(arr[i]);
    }
    return magnitudeSum * signProduct;
}

//Given a grid with N rows and N columns (N >= 2) and a positive integer k, 
// each cell of the grid contains a value. Every integer in the range [1, N * N]
// inclusive appears exactly once on the cells of the grid.
// You have to find the minimum path of length k in the grid. You can start
// from any cell, and in each step you can move to any of the neighbor cells,
// in other words, you can go to cells which share an edge with you current
// cell.
// Please note that a path of length k means visiting exactly k cells (not
// necessarily distinct).
// You CANNOT go off the grid.
// A path A (of length k) is considered less than a path B (of length k) if
// after making the ordered arrays of the values on the cells that A and B go
// through (let's call them lst_A and lst_B), lst_A is lexicographically less
// than lst_B, in other words, there exist an integer index i (1 <= i <= k)
// such that lst_A[i] < lst_B[i] and for any j (1 <= j < i) we have
// lst_A[j] = lst_B[j].
// It is guaranteed that the answer is unique.
// Return an ordered array of the values on the cells that the minimum path go through.
// Examples:    
// >>> minPath([[1, 2, 3], [4, 5, 6], [7, 8, 9]], 3)
// [1, 2, 1]
// >>> minPath([[5, 9, 3], [4, 1, 6], [7, 8, 2]], 1)
// [1]
function minPath(grid, k){  
    let n = grid.length;
    let visited = new Array(n).fill().map(() => new Array(n).fill(false));
    let directions = [[0, 1], [1, 0], [-1, 0], [0, -1]];
    let pq = [];
    for(let i=0; i<n; i++){
        for(let j=0; j<n; j++){
            pq.push([grid[i][j], i, j]);
        }
    }
    pq.sort((a,b) => a[0]-b[0]);
    while(k--){
        let [val, x, y] = pq.shift();
        if(visited[x][y]) continue;
        visited[x][y] = true;
        if(k === 0) return [val];
        for(let [dx, dy] of directions){
            let nx = x + dx, ny = y + dy;
            if(nx >= 0 && nx < n && ny >= 0 && ny < n && !visited[nx][ny]){
                pq.push([grid[nx][ny], nx, ny]);
            }
        }
    }
}

//Everyone knows Fibonacci sequence, it was studied deeply by mathematicians in 
// the last couple centuries. However, what people don't know is Tribonacci sequence.
// Tribonacci sequence is defined by the recurrence:
// tri(1) = 3
// tri(n) = 1 + n / 2, if n is even.
// tri(n) =  tri(n - 1) + tri(n - 2) + tri(n + 1), if n is odd.
// For example:
// tri(2) = 1 + (2 / 2) = 2
// tri(4) = 3
// tri(3) = tri(2) + tri(1) + tri(4)
// = 2 + 3 + 3 = 8 
// You are given a non-negative integer number n, you have to a return an array of the 
// first n + 1 numbers of the Tribonacci sequence.
// Examples:
// >>> tri(3)
// [1, 3, 2, 8]
function tri(n){  
    let arr=[1];
    for(let i=1;i<=n;i++){
        if(i%2===0){
            arr.push((i/2)+1);
        }
        else{
            arr.push(arr[i-1]+arr[i-2]+arr[i+1]);
        }
    }
    return arr;
}

//Given a positive integer n, return the product of the odd digits.
// Return 0 if all digits are even.
// For example:
// >>> digits(1)
// 1
// >>> digits(4)
// 0
// >>> digits(235)
// 15
function digits(n){ 
    let result = 1;
    while (n > 0){
        let digit = n % 10;
        if(digit % 2 !== 0){
            result *= digit;
        }
        n = Math.floor(n / 10);
    }
    return result === 1 ? 0 : result;
}

//Create a function that takes a string as input which contains only square brackets.
// The function should return true if and only if there is a valid subsequence of brackets 
// where at least one bracket in the subsequence is nested.
// >>> is_nested("[[]]")
// true
// >>> is_nested("[]]]]]]][[[[[]")
// false
// >>> is_nested("[][]")
// false
// >>> is_nested("[]")
// false
// >>> is_nested("[[][]]")
// true
// >>> is_nested("[[]][[")
// true
function is_nested(string){  
    let stack = [];
    for(let i=0;i<string.length;i++){
        if(string[i]==="["){
            stack.push(string[i]);
        }
        else{
            if(stack.length===0){
                return false;
            }
            stack.pop();
        }
    }
    return stack.length!==0;
}

