// true
// >>> check_if_last_char_is_a_letter("apple pi e ")
// false
// >>> check_if_last_char_is_a_letter("")
// false
function check_if_last_char_is_a_letter(txt){ 
    // Check if the string is empty or ends with a space
    if (txt === "" || txt.endsWith(" ")) {
        return false;
    }
    // Get the last character of the string
    let lastChar = txt.charAt(txt.length - 1);
    // Check if the last character is an alphabet letter
    if (lastChar >= 'a' && lastChar <= 'z' || lastChar >= 'A' && lastChar <= 'Z') {
        // Check if the second last character is a space or does not exist
        let secondLastChar = txt.charAt(txt.length - 2);
        if (secondLastChar === " " || secondLastChar === "") {
            return true;
        }
    }
    return false;
}

