//Create a function that returns an array (a, b), where 'a' is
// the largest of negative integers, and 'b' is the smallest
// of positive integers in an array.
// If there is no negative or positive integers, return them as undefined.
// Examples:
// >>> largest_smallest_integers([2, 4, 1, 3, 5, 7])
// [undefined, 1]
// >>> largest_smallest_integers([])
// [undefined, undefined]
// >>> largest_smallest_integers([0])
// [undefined, undefined]
function largest_smallest_integers(lst){ 
    let maxNegative = -Infinity;
    let minPositive = Infinity;

    for(let i=0; i<lst.length; i++){
        if(lst[i] < 0 && lst[i] > maxNegative){
            maxNegative = lst[i];
        }
        else if(lst[i] > 0 && lst[i] < minPositive){
            minPositive = lst[i];
        }
    }

    if(maxNegative === -Infinity) {
        maxNegative = undefined;
    }
    if(minPositive === Infinity) {
        minPositive = undefined;
    }

    return [maxNegative, minPositive];
}

